<script>
	function enviarFormulario() {
		document.querySelector('input[type="submit"]').disabled = false;
		return true;
	}

	function validacao() {
		var elements = document.querySelectorAll("a, p, h1, img");
		var id = document.getElementById("container-img");
		var disabled_teste = document.getElementById("botao-download");

		elements.forEach(function(element) {
			element.classList.add("naoselecionar");
		});
		id.classList.add("inabilitado");
		disabled_teste.setAttribute("disabled", "disabled");
	}

	function downloadimage() {
		var container = document.getElementById("container-img");
		container.style.boxShadow = 'none';
		container.style.border = 'none';
		html2canvas(container, {
			allowTaint: true,
		}).then(function(canvas) {
			var link = document.createElement("a");
			document.body.appendChild(link);
			link.download = "assinatura_email.png";
			link.href = canvas.toDataURL();
			link.target = "_self"; // Abre a imagem na mesma janela
			link.click();
		});
		container.style.border = '#8d8d8d 1px solid';
		container.style.boxShadow = '3px 5px 10px gray';
	}

	function reduzNome() {
		var campo_nome = document.getElementById("nome").value;
		var nome_completo = campo_nome.split(" ");
		var NomeFinal = nome_completo[0] + " " + nome_completo.at(-1);
		var nome_assinatura = document.getElementById("nome-img");
		nome_assinatura.innerText = NomeFinal;
		alert("Nome foi reduzido devido seu tamanho!", "alert-warning");
	}

	function max_chars() {
		const valor_maximo = 30;
		var texto = document.getElementById("chars");
		var campo_nome = document.getElementById("nome");
		var valor_name = campo_nome.value;
		var chars = valor_name.length;
		var valor_final = valor_maximo - chars;
		if (valor_final < 0) {
			reduzNome();
			campo_nome.style.borderColor = "rgb(255, 94, 0)";
			campo_nome.style.color = "rgb(255, 94, 0)";
			texto.style.color = "rgb(255, 94, 0)";
			texto.innerText = "Nome reduzido a primeiro nome e ultimo sobrenome.";
		} else {
			campo_nome.style.borderColor = "#ced4da";
			campo_nome.style.color = "#495057";
			texto.style.color = "#000";
			texto.innerText =
				"Letras/Caracteres restantes indicadas " + valor_final + ".";
		}
	}

	function setupPhoneMaskOnField(selector) {
		var inputElement = $(selector);

		setCorrectPhoneMask(inputElement);
		inputElement.on("input, keyup", function() {
			setCorrectPhoneMask(inputElement);
		});
	}

	addEventListener("input", () => {
		var valor_phone = document.getElementById('telefone');
		valor_phone.value = phoneMask(valor_phone.value);


		var valor = document.querySelectorAll('#nome, #cargo');
		var campo_nome = document.querySelectorAll("#nome-img, #cargo-img");
		var valor_email = document.getElementById('email_inc').value;
		var campo_email = document.getElementById('email');
		campo_email.innerText = valor_email;
		var valor_fone = document.getElementById('telefone').value;
		let result = valor_fone.replace(/\(|\)/g, "");
		var campo_fone = document.getElementById('telefone-img');
		campo_fone.innerText = 'Tel.: 55 ' + result;

		valor.forEach(function(valores, index) {
			campo_nome[index].innerText = valores.value;
		});

		max_chars();
		validacao();

	});

	const phoneMask = (value) => {
		if (!value) return ""
		value = value.replace(/\D/g, '')
		value = value.replace(/(\d{2})(\d)/, "($1) $2")
		value = value.replace(/(\d)(\d{4})$/, "$1.$2")
		return value
	}
	addEventListener("change", () => {

		var valor_andar = document.getElementById('endereco').value;
		var campo_andar = document.getElementById('local-img');
		campo_andar.innerText = 'Rua São Bento, 405 | ' + valor_andar + '° andar';

		var valor_setor = document.getElementById('unidade').value;
		var campo_setor = document.getElementById('setor');
		campo_setor.innerText = valor_setor + ' /';

	});

	document.getElementById('telefone').addEventListener('paste', function(event) {
		// Impedir a colagem de mais de 10 caracteres
		var clipboardData = event.clipboardData || window.clipboardData;
		var pastedText = clipboardData.getData('text');

		if (pastedText.length > 0) {
			event.preventDefault();
			this.value = "";
			alert("Não é possivel colar no campo de telefone", "alert-warning");
		}
	});

	$(document).ready(function() {
		max_chars();
		var recarregou = sessionStorage.getItem("recarregou");
		if (recarregou === undefined) {
			$("#nome-img").bind("copy", function(event) {
				event.preventDefault();
			});
		}
		if (recarregou) {
			var elements = document.querySelectorAll(
				"a, p, h1, img, #container-img, #botao-download, body"
			);

			elements.forEach(function(element) {
				element.classList.remove("naoselecionar");
				element.classList.remove("inabilitado");
				element.removeAttribute("disabled");
			});
			var botao = document.querySelector('.container-botao');
			botao.style.display = 'flex';
			sessionStorage.removeItem("recarregou");
		}

	});
</script>